CREATE TABLE IF NOT EXISTS labs (id INT AUTO_INCREMENT PRIMARY KEY, dept VARCHAR(50) NOT NULL, lab_name VARCHAR(255) NOT NULL, configurations TEXT, usage TEXT, image_url TEXT, status ENUM('active', 'inactive', 'maintenance') DEFAULT 'active', created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, created_by VARCHAR(255), updated_by VARCHAR(255))
